import {createSlice, createAsyncThunk} from '@reduxjs/toolkit'
import axios from 'axios'
// const axios = require('axios')


export const getProducts = createAsyncThunk('get-products', async(data , thunkAPI) => {
  try {
    const res = await axios.get(`http://localhost:4000/product/get${data}`)
    return res.data
  } catch (error) {
   return thunkAPI.rejectWithValue(error)
  }
})

const initialState = {
  product: [],
  isSuccess: false,
  isLoading: false,
  isError: false
}

export const slice = createSlice({
  name: 'slice', 
  initialState, 
  reducers: {},
  extraReducers: (b) => 
  b
  .addCase(getProducts.fulfilled, (state, action) => {
    state.product = action.payload
  })

})

export default slice.reducer






