import style from './Products.module.css'
import { useDispatch, useSelector } from 'react-redux'
import { useEffect, useState } from 'react'
import { getProducts } from '../feature/productSlice'
import { addToCart } from '../store/userSlice'
import { useNavigate } from 'react-router-dom'

const Products = () => {
  const dispatch = useDispatch()
  const navigate = useNavigate()
  const buyer = JSON.parse(localStorage.getItem('user'))

  useEffect(() => { 
    dispatch(getProducts())
  }, [])

  const state = useSelector(state => state.product.product)

  const clickHandler = (ev) => {
    navigate(`/product/${ev}`)
  }

  const addToCartHandler = (theBookId) => {
    console.log(theBookId);
    const find = user.cart.find(ev => {
      return theBookId === ev.bookId
    })
    console.log(find);
