    //    return;
    // }
    // const alreadyExit = buyer.cart.some(userCart => {
    //   return userCart.bookId === theBookId
    // })
    // if (alreadyExit){
    //   dispatch(addToCart({ bookId: theBookId, userId: buyer._id, userCart: buyer.cart, alreadyExit: true}))
    // } else {
    //   dispatch(addToCart({ bookId: theBookId, userId: buyer._id, userCart: buyer.cart, alreadyExit: false}))
    // }
  }

  console.log(buyer.cart);

  const map = state?.toReversed().map(ev => (
    <div key={ev._id} className={style.map}>
      <img src={ev.image} alt='book' />
      <p>Book name: {ev.book}</p>
      <p>Author: {ev.author}</p>
      <p>price: {ev.price}</p>
      <p>Sender: {ev.sender}</p>
      <div className='d-f g-10'>
        <button onClick={() => clickHandler(ev._id)}>Detail</button>
        <button onClick={() => addToCartHandler(ev._id)}>Add To Cart</button>
      </div>
    </div>
  ))


  return (
    <div className={style.body} id={map.length === 0 ? 'loading' : ''}>
      {map}
    </div>
  )
}
export default Products
