import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
// const productService = require("./productService")
import productService from "./productService";

const initialState = {
  product: [],
  isError: false,
  isLoading: false,
  isSuccess: false,
}


export const getProducts = createAsyncThunk('getProducts-books', async (thunkAPI) => {
  try {
    return await productService.getS()
  } catch (error) {
    console.log(error);
    return thunkAPI.rejectWithValue(error)
  }
})

export const addBook = createAsyncThunk('add-book', async (data, thunkAPI) => {
  try {
    console.log('slice', data);
    return await productService.addS(data)
  } catch (error) {
    return thunkAPI.rejectWithValue(error)
  }
})


export const productSlice = createSlice({
  name: 'product',
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder

    .addCase(getProducts.pending, (state) => {
      state.isLoading = true
    })
    .addCase(getProducts.fulfilled, (state, action) => {
      state.isLoading = false
      state.isError = false
      state.isSuccess = true
      state.product = action.payload
    })
    .addCase(getProducts.rejected, (state, action) => {
      state.isLoading = false
      state.isSuccess = false
      state.isError = true
    })



      .addCase(addBook.pending, (state) => {
        state.isLoading = true
      })
      .addCase(addBook.fulfilled, (state, action) => {
        state.isLoading = false
        state.isError = false
        state.isSuccess = true
        state.product = action.payload
        alert('Book added successfully')
      })
      .addCase(addBook.rejected, (state, action) => {
        state.isLoading = false
        state.isSuccess = false
        state.isError = true
        alert('reject')
      })

  }
})

export default productSlice.reducer
