import { MyContext } from "../store/context";
import { data } from "./data"
import style from './MainBox1.module.css'
import { useContext } from "react"
import Header from './Header'


function MainBox1() {

    const ctx = useContext(MyContext)

    const clickHadnler = (ev) => {
        const p = {
            id: ev.id,
            name: ev.name,
            price: ev.price,
            description: ev.description,
            image: ev.image,
        }
        ctx.add(p);
    }


    const dataMap = data.map(ev => (
        <div key={ev.id} className={style.map}>

            <img src={ev.image} alt="Product" />
            <p>{ev.name}</p>
            <p>{ev.price}</p>
            <p>{ev.description}</p>

            <div className={style.buttons}>
                <div><button onClick={() => clickHadnler(ev)}>-</button><button onClick={() => clickHadnler(ev)}>+</button></div>
                <input defaultValue={1} min={1} max={5} type="number" />
            </div>

        </div>
    ))
    return (
        <div className={style.box}>
            <Header />

            {dataMap}

        </div>
    )
}
export default MainBox1
