import { useState } from 'react'
import style from './Header.module.css'
import { useNavigate } from 'react-router'

function Header() {
  const [search, setSearch] = useState()
  const nav = useNavigate()

  const submitHandler = (ev) => {
    ev.preventDefault()
    setSearch(ev.target.value)
  }

  const clickHandler = async(ev) => {
    ev.preventDefault()
    await nav(search)
    window.location.reload()
  }

  return (
    <div className={style.body}>

      <div className={style.left}>
        <a href='/'>Home</a>
        <a href='/q=sport'>Sport</a>
        <a href='/q=tech'>Tech</a>
        <a href='/q=economy'>Economy</a>
      </div>

      <form className={style.search} onSubmit={clickHandler}>
        <input type="text" onChange={submitHandler} onSubmit={clickHandler} />
        <a href={`q=${search}`}>search</a>
      </form>

      <p>something</p>
    </div>
  )
}
export default Header
