import style from './Product.module.css'
import { useDispatch, useSelector } from 'react-redux'
import { useEffect } from 'react'
import { get } from '../feature/productSlice'
import { useLocation, useNavigate } from 'react-router-dom'

const Product = () => {
  const location = useLocation()
  const dispatch = useDispatch()

  const path = location.pathname
  const productId = path.split("/")[2]

  useEffect(() => {
    dispatch(get())
  }, [])

  const state = useSelector(state => state.product.product)

  const find = state?.find(ev => {
    return ev._id === productId
  })
  console.log(find);




  return (
    <div className={style.body}>
      <div className={style.map}>
      <img src={find.image} alt='book' />
      <p>Book name: {find.book}</p>
      <p>Author: {find.author}</p>
    </div>
    </div>
  )
}
export default Product
