import {useState} from 'react'
import './Register.css'

function Register(){
  const [secure, setSecure] = useState('')

  const passHandler = (ev) => {
    setSecure(ev.target.value)
    // const pass = ev.target.value
    // console.log(pass.length);
    // if(pass.length === 0){
    //   setSecure('')
