    // if(pass.length <= 4 && pass.length > 0) {
    //   setSecure('weak')
    // }
    // if(pass.length > 4 && pass.length <= 8){
    //   setSecure('moderate')
    // }
    // if(pass.length > 8){
    //   setSecure('strong')
    // }

  }

  return (
    <div className="register">

      <h1>Register</h1>
      <input placeholder="Name"/>
      <input placeholder="Email"/>
      <input placeholder="Password" onChange={(ev) => passHandler(ev)} />

      <div className="pass" >
      <div className={secure === 'weak' ? secure : 'white'} />
      <div className={secure === 'moderate' ? secure : 'white'} />
      <div className={secure.length > 8 ? secure : ''} />
      </div>

      <input placeholder="Password"/>

    </div>
  )
}
export default Register
