import axios from "axios";
const {URL} = require("./URL")

const getS = async () => {
  const response = await axios.get(`${URL}/user/get`);
  return response.data;
};

const registerS = async (data) => {
  const response = await axios.post(`${URL}/user/register`, data);
  return response.data;
};

const signInS = async (data) => {
  console.log('service', data);
  // const response = await axios.post(`${URL}/user/sing-in`, data);
  const response = await axios.post("http://localhost:4000/user/sign-in", data);
  return response.data;
};
const desS = async (data) => {
  const response = await axios.put(`${URL}/user/des`, data);
  return response.data;
};
const messageS = async (data) => {
  const response = await axios.put(`${URL}/user/message`, data);
  return response.data;
};

const userService = {
  getS,
  registerS,
  signInS,
  desS,
  messageS
};

export default userService;
