import style from './Account.module.css'
import {useRef} from 'react'
import {useDispatch, useSelector} from 'react-redux'
import {register} from '../store/userSlice'
import {getUsers} from '../store/userSlice'
import { useEffect } from 'react'

function Account() {
 const user = JSON.parse(localStorage.getItem('user'))
 const emailRef = useRef()
 const nameRef = useRef()
 const passRef = useRef()
  const dispatch = useDispatch()




 useEffect(() => {
  dispatch(getUsers())
},[])

const state = useSelector(state => state.user.user)
console.log('state',state);

const find = state.find(ev => {
  return ev.email === ev
})
console.log(find);





 const submitHandler = (ev) => {
ev.preventDefault()
const user = {name: nameRef.current.value, email: emailRef.current.value, password: passRef.current.value, }
dispatch(register(user));
 }

  return(
    <div className={style.body}>
      {user && <p>Welcome {state.name}</p>}

      {!user && 
      <form onSubmit={submitHandler} className={style.signUp}>
        <h1>Sign Up</h1>
        <input ref={nameRef} placeholder="name" type="string" />
        <input ref={emailRef} placeholder="email" type="email" />
        <input ref={passRef} placeholder="password" type="string" />
        <button>Create Account</button>
      </form>
        }
        <h1>Do you have account? Sign In</h1>
    </div>
  )
}
export default Account
