export const MyContext = createContext()

const [data, setData] = useState({})

  const val = {
    product : data,
    add : (ev) => {
      setData(ev);
    },
    del: (ev) => {
      console.log(ev);
    }
  }

const Prov = (props) => {
  return(
    <MyContext.Provider value={val}>
     {props.children}
  </MyContext.Provider>
    )
}

export default Prov
