import style from './Body.module.css'
import {useEffect, useState} from 'react'

function Body() {
  const [data, setData] = useState()

  // GET NEWS FROM GUARDIAN
  useEffect(() => {

    async function newsData(){
      const res = await fetch(`https://content.guardianapis.com/search?api-key=ac83f541-8a6f-49b6-a8fe-7af22e4f4924`)
      const d = await res.json()
      console.log(d);

      const dataMap = d.response.results.map(ev => (
        <div key={ev.id} className={style.title}>
          <a href={ev.webUrl}>{ev.webTitle}</a>
          <img src={ev.apiUrl} />
        </div>
    )
  )

    setData(dataMap)
}

newsData()
}, [])


  return(
    <div>
      {data}
    </div>
  )
}
export default Body
