import axios from "axios";
const {URL} = require("./URL")

const getS = async () => {
  const response = await axios.get(`${URL}/user/get`);
  return response.data;
};

const registerS = async (data) => {
  const response = await axios.post(`${URL}/user/register`, data);
  return response.data;
};

const signInS = async (data) => {
  const response = await axios.post(`${URL}/user/sign-in`, data);
  return response.data;
};
const addToCartS = async(data) => {
  const response = await axios.put(`${URL}/user/add-to-cart`,data)
  return response.data
}

const orderS = async(data) => {
  const response = await axios.put(`${URL}/user/order`,data)
  return response.data
}

const deleteFromCartS = async(data) => {
  const response = await axios.put(`${URL}/user/delete-from-cart`,data)
  .then(alert('a message ser'))
  return response.data
}  

const deleteAccountS = async(data) => {
  const response = await axios.post(`${URL}/user/delete-account`,data)
  .then(window.location.reload())
  return response.data
}  
const userService = {
  getS,
  registerS,
  signInS,
  addToCartS,
  orderS,
  deleteFromCartS,
  deleteAccountS,
};

export default userService;
