import style from './Search.module.css'
import { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'

function Search() {
  const [guardian, setGuardian] = useState()
  const [ny, setNy] = useState()
  const [newsApi, setNewsApi] = useState()
  const [sort, setSort] = useState('relevance')
  const location = useLocation()
  const nav = useNavigate()
  const [totalPages, setTotalPages] = useState()
  // const [category, setCategory] = useState()
  let [currentPage, setCurrentPage] = useState(2)

  const keyword = 'q='
  const category = 'tag=sport/sport'
  // const keyword = location.pathname.split('&')[1]
  // const category = location.pathname.split('&')[2]
  // console.log(keyword, category);
  console.log(location.pathname.split('q='));
  console.log(location.pathname.split('='));
  console.log(location.pathname.split('?'));
  console.log(location.pathname.split('&'));

  console.log('->' , location.href, '<-');
  console.log('->' , window.location.href, '<-');
  console.log('->' , window.location.href, '<-');
  console.log('->' , window.location.assign('cat'), '<-');


  // FETCHING THE NEWS
  useEffect(() => {

    //GUARDIAN
    async function newsData1() {
      // const GUARDIAN_API = await fetch(`https://content.guardianapis.com/search?${keyword}&page=${currentPage}&tag=${category}&api-key=ac83f541-8a6f-49b6-a8fe-7af22e4f4924`)
      // const GUARDIAN_API = await fetch(`https://content.guardianapis.com/search?${keyword}&tag=${category}&page=${currentPage}&api-key=ac83f541-8a6f-49b6-a8fe-7af22e4f4924`)
      // const GUARDIAN_API = await fetch(`https://content.guardianapis.com/search?${keyword}&tag=${category}/${category}&from-date=2014-01-01&api-key=ac83f541-8a6f-49b6-a8fe-7af22e4f4924`)
      // const GUARDIAN_API = await fetch(`https://content.guardianapis.com/search?${keyword}${category}&from-date=2014-01-01&api-key=ac83f541-8a6f-49b6-a8fe-7af22e4f4924`)
      // const GUARDIAN_API = await fetch(`https://content.guardianapis.com/search?${keyword}&${category}&from-date=2014-01-01&api-key=ac83f541-8a6f-49b6-a8fe-7af22e4f4924`)
      // const GUARDIAN_API = await fetch(`https://content.guardianapis.com/tags?${keyword}&order-by=${sort}&api-key=ac83f541-8a6f-49b6-a8fe-7af22e4f4924`)
      // const GUARDIAN_API = await fetch(`https://content.guardianapis.com/search?${keyword}&${category}&order-by=${sort}&api-key=ac83f541-8a6f-49b6-a8fe-7af22e4f4924`)
      const GUARDIAN_API = await fetch(`https://content.guardianapis.com/search?${keyword}&${category}&order-by=${sort}&page=${currentPage}&api-key=ac83f541-8a6f-49b6-a8fe-7af22e4f4924`)
      console.log(GUARDIAN_API);
      const g = await GUARDIAN_API.json()

      // MAP - (SHOWING THE RESULT)
      const guardianMap = g.response.results.map(ev => (
        <div key={ev.id} className={style.title}>
          <a href={ev.webUrl}>{ev.webTitle}</a>
          <p>{ev.webUrl.split('/')[2]}</p>
        </div>
      ))
      setGuardian(guardianMap)

      //PAGINATION
      setTotalPages(g.response.pages)
    }
    newsData1()



    //NEW YORK TIMES
    // async function newsData2() {
    //   const NY_API = await fetch(`https://api.nytimes.com/svc/search/v2/articlesearch.json?${keyword}?page=${currentPage}&api-key=pFRWaAYSzJcy93VdWbwBz8squ4LkfIeq`)
    //   const n = await NY_API.json()
    //   const nyMap = n.response.docs.map(ev => (
    //     <div key={ev._id} className={style.title}>
    //       <a href={ev.web_url}>{ev.abstract}</a>
    //       <p>{ev.web_url.split('/')[2]}</p>
    //     </div>
    //   ))
    //   setNy(nyMap)
    // }
    // newsData2()

    // NEWS_API
    // async function newsData3() {
    //   const NEWSAPI_API = await fetch(`https://newsapi.org/v2/everything?q=a&from=2024-04-26&sortBy=popularity&apiKey=d7116b1cbcfd4fdd9e28ca60ba30ec7c`)
    //   const NEWSAPI_API = await fetch('https://newsapi.org/v2/everything?q=apple&from=2024-04-29&to=2024-04-29&sortBy=popularity&apiKey=d7116b1cbcfd4fdd9e28ca60ba30ec7c')
    //   const na = await NEWSAPI_API.json()
    //   const newsApiMap = na.articles.map(ev => (
    //     <div key={Math.random()} className={style.title}>
    //       <a href={ev.url}>{ev.title}</a>
    //       <p>{ev.url.split('/')[2]}</p>
    //     </div>
    //   ))
    //   setNewsApi(newsApiMap)


    // }
    // newsData3()

  }, [sort, currentPage])

  const sourceHandler = (ev) => {
    nav(`/&q=${keyword}&tags=${category}&source=${ev.target.value}`)
    // window.location.reload()
  }

  const categoryHandler = (ev) => {
    nav(`/&${keyword}&tags=${ev.target.value}`)
    window.location.reload()
  }

  const sortHandler = (ev) => {
    setSort(ev.target.value)
    // window.location.reload()
  }

  return (
    <div className={style.body}>


     {/*  FILTER  (sorby)*/}
      <div className={style.filter}>

        <div>
          <h1>Sort by</h1>
          <select onChange={sortHandler}>
            <option value={'relevance'}>relevance (default)</option>
            <option value={'newest'}>newest</option>
            <option value={'oldest'}>oldest</option>
            <option></option>
          </select>
        </div>

        <h1>from to</h1>

     {/*  FILTER  (category)*/}
        <div>
          <h1>Category, Tag</h1>
          <select defaultValue={category} onChange={categoryHandler}>
            {/* <option value={category} disabled>{category === '&' ? 'category' : category.split('=')[1].split('/')[1]}</option> */}
            <option value={'sport'}>Sport</option>
            <option value={'technology'}>Technology</option>
            <option value={'politics'}>Politics</option>
          </select>
        </div>

      </div>


     {/*  FILTER  (sourse)*/}
      <div className={style.checkboxBody}>

<div>
        <div className={style.checkbox}>
          <input type='checkbox' onChange={sourceHandler} value={'www.theguardian.com'} />
          <label>Guardian</label>
        </div>

        <div className={style.checkbox}>
          <input type='checkbox' />
          <label>New York Times</label>
        </div>

</div>

<div>

        <div className={style.checkbox}>
          <input type='checkbox' onChange={sourceHandler} value={'www.theguardian.com'} />
          <label>Guardian</label>
        </div>

        <div className={style.checkbox}>
          <input type='checkbox' />
          <label>New York Times</label>
      </div>
</div>

      </div>


      <div>{guardian}</div>
      <div>{ny}</div>
      {/* <div>{newsApi}</div> */}


      {/* PAGINATION */}
      {totalPages &&
      <div className={style.pages}>
        <p onClick={() => (currentPage > 1 && setCurrentPage(currentPage - 1))}>prevous</p>
        <div className={style.middlePages}>
          <h3 onClick={() => (setCurrentPage(1))} style={{ color: currentPage === 1 && 'blue', fontSize: currentPage === 1 && '23px' }}>1</h3>
          <h3></h3>
          <h3 onClick={() => (setCurrentPage(currentPage + 3))} style={{ display: currentPage < 3 && 'none' }}>{(currentPage - 1) < totalPages && currentPage - 1}</h3>
          <h2 className={style.currentPage} style={{ display: currentPage < 2 && 'none' }}>{currentPage}</h2>
          <h3 onClick={() => (setCurrentPage(currentPage + 1))}>{currentPage < totalPages && currentPage + 1}</h3>
          <h3 onClick={() => (setCurrentPage(currentPage + 2))}>{(currentPage + 1) < totalPages && currentPage + 2}</h3>
          <h3></h3>
          <h3 onClick={() => (setCurrentPage(totalPages))}>{totalPages}</h3>
        </div>
        <p onClick={() => (setCurrentPage(currentPage + 1))}>Next</p>
      </div>
      }

    </div>
  )
}
export default Search
