import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import userService from "./userService";

const initialState = {
  user: [],
  isError: false,
  isLoading: false,
  isSuccess: false,
};

export const getUsers = createAsyncThunk(
  "get-all-users",
  async (user, thunkAPI) => {
    try {
      return await userService.getS(user);
    } catch (e) {
      return thunkAPI.rejectWithValue(e);
    }
  }
);

export const register = createAsyncThunk(
  "register-new-user",
  async (data, thunkAPI) => {
    try {
      return await userService.registerS(data);
    } catch (e) {
      return thunkAPI.rejectWithValue(e);
    }
  }
);

export const singIn = createAsyncThunk(
  "sing-in",
  async (data, thunkAPI) => {
    try {
      console.log('selice', data);
      return await userService.signInS(data);
    } catch (e) {
      return thunkAPI.rejectWithValue(e);
    }
  }
);
export const descSlice = createAsyncThunk(
  "edit-description",
  async (data, thunkAPI) => {
    try {
      return await userService.desS(data);
    } catch (e) {
      return thunkAPI.rejectWithValue(e);
    }
  }
);

export const messageSlice = createAsyncThunk(
  "message-user",
  async (data, thunkAPI) => {
    try {
      return await userService.messageS(data);
    } catch (e) {
      return thunkAPI.rejectWithValue(e);
    }
  }
);

export const addToCart = createAsyncThunk('add-to-cart', async (data, thunkAPI) => {
  try {
    console.log('slice', data);
    return await userService.addToCartS(data)
  } catch (error) {
    return thunkAPI.rejectWithValue(error)
  }
})


export const userSlice = createSlice({
  name: "user",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(getUsers.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(getUsers.fulfilled, (state, action) => {
        state.isError = false;
        state.isLoading = false;
        state.isSuccess = true;
        state.user = action.payload;
      })
      .addCase(getUsers.rejected, (state, action) => {
        state.isLoading = false;
        state.isSuccess = false;
        state.isError = true;
      })

      .addCase(register.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(register.fulfilled, (state, action) => {
        state.isError = false;
        state.isLoading = false;
        state.isSuccess = true;
        // state.user = action.payload;
        alert('User Created Successfully')
      })
      .addCase(register.rejected, (state, action) => {
        state.isLoading = false;
        state.isSuccess = false;
        state.isError = true;
        alert('Something went wrong')
      })

      .addCase(singIn.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(singIn.fulfilled, (state, action) => {
        state.isError = false;
        state.isLoading = false;
        state.isSuccess = true;
        localStorage.setItem('user', JSON.stringify(action.payload))
        alert('You are logged in successfully')
        window.location.reload()
      })
      .addCase(singIn.rejected, (state, action) => {
        state.isLoading = false;
        state.isSuccess = false;
        state.isError = true;
        if (action?.payload?.response?.status === 404) {
        return  alert('Invalid Authentication. Do you have any account?')
        }
        if (action?.payload?.response?.status === 401) {
         return alert("Username and password not match")
        }
        else alert("Something went wrong!")
      })


      .addCase(addToCart.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(addToCart.fulfilled, (state, action) => {
        state.isError = false;
        state.isLoading = false;
        state.isSuccess = true;
        // state.user = action.payload;
        console.log(action.payload,'--------------------------------------------------------------');
        // localStorage.setItem('user', JSON.stringify(a))
        // window.location.reload()
      })
      .addCase(addToCart.rejected, (state, action) => {
        state.isLoading = false;
        state.isSuccess = false;
        state.isError = true;
      })


  },
});

export default userSlice.reducer;
