import { useContext, useState } from "react";
import style from './Header.module.css'
import { MyContext } from "../store/context";

function Header() {
    const [date, setDate] = useState(new Date())
    const [showCart, setShowCart] = useState(false)
    const ctx = useContext(MyContext)
    console.log(ctx.product);

    // setTimeout(() => {
    //     setDate(new Date())
    // }, 1000);

    const map = ctx.product.map(ev => 
        <div className={style.map} key={ev.id}>
            <p>{ev.name}</p>
            <p>{ev.price}</p>
            <p>{ev.description}</p>
            <img src={ev.image} width="40px" />
        </div>
        )

    return <div className={style.header}>
        <div>
          <p>{date.getFullYear()} - {date.getMonth()} - {date.getDay()}</p>
          <p>{date.getHours()} : {date.getMinutes()} : {date.getSeconds()}</p>
        </div>
        <button onClick={() => setShowCart(true)}>Your Cart 0</button>

        {showCart && <div className={style.headerCart}>{map}</div>}
        {showCart && <div className={style.headerBack} onClick={() => setShowCart(false)} />}
    </div>
}

export default Header
