import style from './Products.module.css'
import { useDispatch, useSelector } from 'react-redux'
import { useEffect } from 'react'
import {get} from '../feature/productSlice'

const Products = () => {
  const dispatch = useDispatch()


  useEffect(() => {
    dispatch(get())
  }, [])

  const state = useSelector(state => state.product.product)
  console.log(state);

  const clickHandler = () => {
    console.log('clicked');
  }

  const map = state?.toReversed().map(ev => (
    <div onClick={clickHandler} key={ev._id} className={style.map}>
      <img src={ev.image} alt='book'/>
      <p>Book name: {ev.book}</p>
      <p>Author: {ev.author}</p>
    </div>
  ))
  // console.log('map is : ',map);


  return (
    <div className={style.body}>
     {map}
    </div>
  )
}
export default Products
