import style from './Products.module.css'
import { useDispatch, useSelector } from 'react-redux'
import { useEffect, useState } from 'react'
import {getProducts} from '../feature/productSlice'
import {useNavigate} from 'react-router-dom'

const Products = () => {
  const dispatch = useDispatch()
const navigate = useNavigate()
const [message, setMessage] = useState("")

  useEffect(() => {
    dispatch(getProducts())
  }, [])

  const state = useSelector(state => state.product.product)
  console.log(state);

  const clickHandler = (ev) => {
    navigate(`/product/${ev}`)
  }

  const map = state?.toReversed().map(ev => (
    <div key={ev._id} className={style.map}>
      <img src={ev.image} alt='book'/>
      <p>Book name: {ev.book}</p>
      <p>Author: {ev.author}</p>
      <p>Sender: {ev.sender}</p>
      <div className='d-f g-10'>
      <button onClick={() => clickHandler(ev._id)}>Detail</button>
      <button onClick={() => clickHandler(ev._id)}>Add To Cart</button>
      </div>
    </div>
  ))

  // if(map.length === 0)  setMessage("No Products found!") 
  if(map.length === 0 ) console.log('it is 0');

  return (
    <div className={style.body}>
      {/* {message} */}
     {map}
    </div>
  )
}
export default Products
