import { data } from "./data"
import style from './MainBox1.module.css'
import Con from '../store/context'
import { useContext } from "react"


function MainBox1() {

   const ctx = useContext(Con)
   console.log(ctx);

    const dataMap = data.map(ev => (
        <div key={ev.id} className={style.map}>

            <div className={style.map1}>
            <img src={ev.image} />

<div className={style.map2}>
<div className={style.namePrice}>
            <p>Name: {ev.name}</p>
            <p>Price: {ev.price}</p>
</div>
            <p className={style.des}>{ev.description}</p>
</div>

            <div className={style.buttons}>
            <div><button>-</button><button>+</button></div>
            <input defaultValue={1} min={1} max={5} type="number"/>
            </div>

            </div>
            {/* <p>{ev.description}</p> */}
        </div>
    ))
    return (
        <div className={style.box}>
            {dataMap}
        </div>
    )
}
export default MainBox1
