import {useEffect, useState} from 'react'
import "./Post.css"

function Post(){
  // upload image
  const [file, setFile] = useState()
  const [preview, setPreview] = useState()

  const [anim, setAnim] = useState(0)

  // upload image
  useEffect(() => {
    if(!file) return 
     const fileReader = new FileReader()
     fileReader.readAsDataURL(file)
     fileReader.onload = () => {
       setPreview(fileReader.result)
     }
  },[file])

  console.log(anim);

  return <div className="post">

    {(anim === 0 || anim === 1) && <div className="div anim" onClick={() => setAnim(1) ,() => console.log('test')}>
    <input type="file" onChange={(ev) => setFile(ev.target.files[0])}/>
    </div> }

   {/* upload image */}
   {preview && <img src={preview} alt='upload' /> }

    {anim === 0 && <h1>OR</h1> }

   {(anim === 0 || anim === 2) && <div className="div" onClick={() => setAnim(2)}>
    <div>upload your song or <h1>beat</h1></div>
    </div>}

  </div>
}
export default Post
