import { useEffect, useState } from 'react'
import style from './Header.module.css'
import { useNavigate } from 'react-router'

function Header() {
  const [search, setSearch] = useState()
  const nav = useNavigate()

  useEffect(() => {

    async function fetchData() {
      const d = await fetch('https://newsapi.org/v2/top-headlines?country=us&apiKey=d7116b1cbcfd4fdd9e28ca60ba30ec7c')
      const data = d.json() 
      console.log(data);
    }
    fetchData()
  },[])

  const submitHandler = (ev) => {
    ev.preventDefault()
    setSearch(ev.target.value)
  }

  const clickHandler = (ev) => {
    ev.preventDefault()
    nav(`q=${search}`)
    window.location.reload()
  }

  return (
    <div className={style.body}>

      <div className={style.left}>
        <a href='/'>Home</a>
        <a href='/q=sport'>Sport</a>
        <a href='/q=tech'>Tech</a>
        <a href='/q=economy'>Economy</a>
      </div>

      <form className={style.search} onSubmit={clickHandler}>
        <input type="text" onChange={submitHandler} onSubmit={clickHandler} />
        <a href={`q=${search}`}>search</a>
      </form>

    </div>
  )
}
export default Header
