import style from './Post.module.css'
import {useEffect, useState} from 'react'

function Post(){
  const [iimg, setImg] = useState({})
  const [iimg2, setImg2] = useState()

  useEffect(() => {
     const fileReader = new FileReader()
     fileReader.readAsDataURL(iimg)
     fileReader.onload = () => {
       setImg2(fileReader.result)
     }
  },[iimg])

  const changeHandler = (ev) =>{
    setImg(ev.target.files[0]);
  }

  return <div className={style.post}>
    <input type="file" id='img' accept='jpg' onChange={(ev) => changeHandler(ev)}/>

    <img src={iimg2} alt='upload img' />

    <h1>OR</h1>

    <p>upload your song or beat</p>
  </div>
}
export default Post
