import {useEffect, useState} from 'react'
import "./Post.css"

function Post(){
  // upload image
  const [file, setFile] = useState()
  const [preview, setPreview] = useState()

  const [stay, setStay] = useState(false)
  // const [active, setActive] = useState('')

  // upload image
  useEffect(() => {
    if(!file) return 
     const fileReader = new FileReader()
     fileReader.readAsDataURL(file)
     fileReader.onload = () => {
       setPreview(fileReader.result)
     }
  },[file])

  return <div className="post">

    <div className={stay === 2 ? 'box left' : 'box'} id={stay === 1 ? 'box right' : 'box'} onClick={() => setStay(1)}>
    <input type="file" onChange={(ev) => setFile(ev.target.files[0])}/>
    </div>

   {/* upload image */}
   {preview && <img src={preview} alt='upload' /> }

    <h1 className={stay ? 'left' : ''}>OR</h1> 

    <div className={stay === 1 ? 'box left' : 'box'} onClick={() => setStay(2)}>
    <div>upload your song or <h1>beat</h1></div>
    </div>

  </div>
}
export default Post
