import style from './Account.module.css'
import { useRef, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { register, singIn } from '../store/userSlice'
import { getProducts } from '../feature/productSlice'
import { useEffect } from 'react'
import { getUsers } from '../store/userSlice'


function Account() {
  const emailRef = useRef()
  const nameRef = useRef()
  const passRef = useRef()
  const inEmailRef = useRef()
  const inPassRef = useRef()

  const dispatch = useDispatch()
  let userLocalStorage = JSON.parse(localStorage.getItem('user'))


  useEffect(() => {
    dispatch(getUsers())
    dispatch(getProducts())
  }, [])

  const userState = useSelector(state => state.user.user)
  const productState = useSelector(state => state.product.product)

  let find = false
  if (userLocalStorage){
     find = userState?.find(ev => {
      return ev.email === userLocalStorage.email
    })
  }

  const findProduct = productState.filter(ev => {
    return ev.sender === userLocalStorage.email
  })
  const productMap = findProduct?.map(ev => (
    <div key={ev._id} className={style.map}>
    <img src={ev.image} alt='book'/>
    <p>Book name: {ev.book}</p>
    <p>Author: {ev.author}</p>
    <p>Sender: {ev.sender}</p>
    <button onClick={() => editHandler(ev._id)}>Detail</button>
  </div>
  ))

  const editHandler = () => {
    console.log('clicked');
  }

  const inHandler = (ev) => {
    ev.preventDefault()
    const userD = { email: inEmailRef.current.value, password: inPassRef.current.value }
    dispatch(singIn(userD))
  }

  const upHandler = (ev) => {
    ev.preventDefault()
    const userD = { name: nameRef.current.value, email: emailRef.current.value, password: passRef.current.value, }
    dispatch(register(userD));
  }

  const signOutHandler = () => {
    localStorage.removeItem('user')
    window.location.reload()
  }

  return (
    <div className={style.body}>


      {find ?
        <div>
          <button onClick={signOutHandler}>Sign out</button>
          <p>Welcome {find.name}</p>
          {productMap}
        </div>
        :
        <>

          <form onSubmit={inHandler} className={style.signUp}>
            <h1>Sign IN</h1>
            {/* <input ref={nameRef} placeholder="name" type="string" /> */}
            <input ref={inEmailRef} placeholder="email" type="string" />
            <input ref={inPassRef} placeholder="password" type="string" />
            <button>Sign in</button>
          </form>

          <form onSubmit={upHandler} className={style.signUp}>
            <h1>Sign Up</h1>
            <input ref={nameRef} placeholder="name" type="string" />
            <input ref={emailRef} placeholder="email" type="string" />
            <input ref={passRef} placeholder="password" type="string" />
            <button>Create Account</button>
          </form>
          {!find && <a >Do you have account? Sign In</a>}
          {find && <a >Sign Up</a>}
        </>


      }




    </div>
  )
}
export default Account
