import MyContext from "../store/context";
import { data } from "./data"
import style from './MainBox1.module.css'
import { useContext, useState } from "react"
import Header from './Header'


function MainBox1() {
    const [dark, setDark] = useState('dark')

    const ctx = useContext(MyContext)
    console.log(ctx);

    const plusHadnler = () => {
        ctx.test('clicked on plus button');
    }


    const dataMap = data.map(ev => (
        <div key={ev.id} className={style.map}>

            <div className={style.map1}>
                <img src={ev.image} alt="Product" />

                <div className={style.map2}>
                    <div className={style.namePrice}>
                        <p>Name: {ev.name}</p>
                        <p>Price: {ev.price}</p>
                    </div>
                    <p className={style.des}>{ev.description}</p>
                </div>

                <div className={style.buttons}>
                    <div><button>-</button><button onClick={plusHadnler}>+</button></div>
                    <input defaultValue={1} min={1} max={5} type="number" />
                </div>

            </div>
        </div>
    ))
    return (
        <div className={style.box}>
            <Header />

            <div className={style.test}>{dataMap}</div>

        </div>
    )
}
export default MainBox1
