import { useEffect, useState } from 'react'
import style from './Header.module.css'
import { useNavigate } from 'react-router'

function Header() {
  const [search, setSearch] = useState()
  const [data, setData] = useState()
  const nav = useNavigate()

  useEffect(() => {

    async function fetchData() {
      const d = await fetch(`https://newsapi.org/v2/everything?q=Apple&from=2024-04-26&sortBy=popularity&apiKey=d7116b1cbcfd4fdd9e28ca60ba30ec7c`)
      const data = await d.json()
      setData(data.articles)
    }
    fetchData()
  }, [])

  const submitHandler = (ev) => {
    ev.preventDefault()
    setSearch(ev.target.value)
  }

  const clickHandler = (ev) => {
    ev.preventDefault()
    nav(`q=${search}`)
    window.location.reload()
  }

  if (!data) return
  // const dataMap = data.map(ev => ev)
  // console.log(dataMap);

  let dataMap = data.map((ev, i) => {
    if (ev.title.includes(search)) {
      return (
        <div style={{display:'grid'}} key={i}>
        <a href={ev.url} style={{padding: '10px 0'}}>{ev.title}</a>
        </div>
      )
    }
  })
  console.log(search);

  return (
    <div className={style.body}>

      <div className={style.left}>
        <a href='/'>Home</a>
        <a href='/q=sport'>Sport</a>
        <a href='/q=tech'>Tech</a>
        <a href='/q=economy'>Economy</a>
      </div>

      <form className={style.search} onSubmit={clickHandler}>
        <div style={{ display: 'grid' }}>
          <input type="text" onChange={submitHandler} onSubmit={clickHandler} />
          <div className={style.module} style={{display: dataMap ? 'block' : 'none'}}>{dataMap}</div>
        </div>
        <a href={`q=${search}`}>search</a>
      </form>


    </div>
  )
}
export default Header
