import { useEffect, useTransition } from 'react'
import style from './Cart.module.css'
import { useDispatch, useSelector } from 'react-redux'
import { getProducts } from '../feature/productSlice'
import { deleteFromCart } from '../store/userSlice'

const Cart = () => {
  const user = JSON.parse(localStorage.getItem('user'))

  let arr = [];
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getProducts())
  }, [])

  const state = useSelector(state => state.product.product)

  const deleteHandler = (bookId) => {
    dispatch(deleteFromCart({userId: user._id, bookId}))
  }


if(!user) return <a href='account'><h1>Sign in</h1></a>
if(!user.cart) return <a href="account">Log in</a>


let sum = 0;
  user.cart.map(cId => { state.filter(eve => { if (eve._id === cId) arr.push(eve)}) })
  const map = arr.map(ev => (
    <div key={ev._id} className={style.map}>
      <img src={ev.image} alt='book' />
      <p>{ev.book}</p>
      <p>{ev.author}</p>
      <h4>${ev.price}</h4>
      <button onClick={() => deleteHandler(ev._id)}>Delete from Cart</button>
      {sum = ev.price}
    </div>
  ))

  return (
    <div className={style.cart} id={map.length === 0 ? 'loading' : ''}>
      {map}
      <div className='d-f a-c'>
      <h1>Total Price: $1,000,000</h1>
      <button>Continue To Order</button>
      </div>
    </div>
  )
}
export default Cart
