import style from './AddProduct.module.css'
import { useRef } from 'react'
import {useDispatch} from 'react-redux'

const AddProduct = () => {
  const dispatch = useDispatch()
  const bookRef = useRef()
  const authorRef = useRef()
  const priceRef = useRef()
  const imageRef = useRef()

  const addHandler = () => {
    const data = { book: bookRef.current.value, author: authorRef.current.value, price: priceRef.current.value, image: imageRef.current.value, }
    console.log(data);
  }

  return (
    <div className={style.body}>
      <div className={style.add}>
        <span>
          <p>Book name</p>
          <input ref={bookRef} />
        </span>
        <span>
          <p>Author name</p>
          <input ref={authorRef}/>
        </span>
        <span>
          <p>Price</p>
          <input ref={priceRef}/>
        </span>
        <span>
          <p>Image</p>
          <input ref={imageRef}/>
        </span>
        <button onClick={addHandler}>OK</button>
      </div>
    </div>
  )
}
export default AddProduct
