import style from './Product.module.css'
import { useDispatch, useSelector } from 'react-redux'
import { useEffect } from 'react'
import { get } from '../feature/productSlice'
import { useLocation, useNavigate } from 'react-router-dom'

const Product = () => {
  const location = useLocation()
  const dispatch = useDispatch()

  const path = location.pathname
  const productId = path.split("/")[2]

  useEffect(() => {
    dispatch(get())
  }, [])

  const state = useSelector(state => state.product.product)

  const find = state?.find(ev => {
    return ev._id === productId
  })
  console.log(find);

  const map = state?.toReversed().map(ev => (
    <div key={ev._id} className={style.map}>
      <img src={ev.image} alt='book' />
      <p>Book name: {ev.book}</p>
      <p>Author: {ev.author}</p>
    </div>
  ))


  return (
    <div className={style.body}>
      {map}
    </div>
  )
}
export default Product
