import {useRef, useState} from 'react'
import './Register.css'

function Register(){
  const [color, setColor] = useState('')
  const [secure, setSecure] = useState(undefined)
  const nameRef = useRef()
  const pass2Ref = useRef()
  const [email, setEmail] = useState('')
  const [foc, setFoc] = useState(false) 


  const passHandler = (ev) => {
    const pass = ev.target.value
    if(pass.length === 0){
      setColor('')
    }
    if(pass.length <= 4 && pass.length > 0) {
      setSecure(0)
      setColor('weak')
    }
    if(pass.length > 4 && pass.length <= 8){
      setSecure(1)
      setColor('moderate')
    }
    if(pass.length > 8){
      setSecure(2)
      setColor('strong')
    }
  }

  const submitHandler = () => {
    console.log(nameRef.current.value);
    console.log(pass2Ref.current.value);
    console.log(email);
  }

  const emailHandler = (ev) => {
    setEmail(ev.target.value);
  }

  return (
    <form className="register" onSubmit={submitHandler}>

      <h1>Register</h1>
      <input placeholder="Name" ref={nameRef} />
      <input placeholder="Email" onChange={(ev) => emailHandler(ev)}/>

      <input placeholder="Password" onChange={(ev) => passHandler(ev)} onBlur={() => console.log('blur')}/> 
     {focus &&  <div className="pass" >
      <div className={color} />
      <div className={secure && color} />
      <div className={secure === 2 ? color : ''} />
      </div>}

      <input placeholder="Password" ref={pass2Ref}/>

      <button>Register</button>

    </form>
  )
}
export default Register
