import style from './Account.module.css'
import {useRef, useState} from 'react'
import {useDispatch, useSelector} from 'react-redux'
import {register, singIn} from '../store/userSlice'


function Account() {
  const [log, setLog] = useState(false)
 const emailRef = useRef()
 const nameRef = useRef()
 const passRef = useRef()
  const dispatch = useDispatch()
  const user = JSON.parse(localStorage.getItem('user'))


  useEffect(() => {
   dispatch(getUsers())
 },[])

 const state = useSelector(state => state.user.user)

 const find = state.find(ev => {
   return ev.email === user.email
 })


const inHandler = (ev) => {
ev.preventDefault()
const user = {email: emailRef.current.value, password: passRef.current.value}
dispatch(singIn(user))
}


 const upHandler = (ev) => {
ev.preventDefault()
const user = {name: nameRef.current.value, email: emailRef.current.value, password: passRef.current.value, }
dispatch(register(user));
setLog(true)
 }

  return(
    <div className={style.body}>
     {'user' && <p>Welcome </p>}
      {log && 
         <form onSubmit={inHandler} className={style.signUp}>
         <h1>Sign IN</h1>
         {/* <input ref={nameRef} placeholder="name" type="string" /> */}
         <input ref={emailRef} placeholder="email" type="email" />
         <input ref={passRef} placeholder="password" type="string" />
         <button>Sign in</button>
       </form>
      }

      {!log && 
      <form onSubmit={upHandler} className={style.signUp}>
        <h1>Sign Up</h1>
        <input ref={nameRef} placeholder="name" type="string" />
        <input ref={emailRef} placeholder="email" type="email" />
        <input ref={passRef} placeholder="password" type="string" />
        <button>Create Account</button>
      </form>
        }
      {!log &&  <a onClick={() => setLog(true)}>Do you have account? Sign In</a> }
      {log &&  <a onClick={() => setLog(false)}>Sign Up</a> }
    </div>
  )
}
export default Account
