import style from './Body.module.css'
import { useEffect, useState } from 'react'

function Body() {
  const [guardian, setGuardian] = useState()
  const [ny, setNy] = useState()
  const [newsApi, setNewsApi] = useState()

  // FETCHING THE DATA
  useEffect(() => {

    async function newsData() {
      const guardian = await fetch(`https://content.guardianapis.com/search?api-key=ac83f541-8a6f-49b6-a8fe-7af22e4f4924`)
      const ny = await fetch('https://api.nytimes.com/svc/search/v2/articlesearch.json?q=election&api-key=pFRWaAYSzJcy93VdWbwBz8squ4LkfIeq')
      const newsApi = await fetch('https://newsapi.org/v2/everything?q=bitcoin&apiKey=d7116b1cbcfd4fdd9e28ca60ba30ec7c')
      console.log(ny);

      const g = await guardian.json()
      const n = await ny.json()
      const na = await newsApi.json()
      console.log(na);

      const guardianMap = g.response.results.map(ev => (
        <div key={ev.id} className={style.title}>
          <a href={ev.webUrl}>{ev.webTitle}</a>
        </div>
      ))

      const nyMap = n.response.docs.map(ev => (
        <div key={ev._id} className={style.title}>
          <a href={ev.web_url}>{ev.abstract}</a>
        </div>
      ))

      const newsApiMap = na.articles.map(ev => (
        <div key={ev.url} className={style.title}>
          <a href={ev.url}>{ev.title}</a>
        </div>
      ))

      setGuardian(guardianMap)
      setNy(nyMap)
      setNewsApi(newsApiMap)

    }

    newsData()
  }, [])


  return (
    <div className={style.body}>

      <div>
        <h1>Guardian</h1>
        {guardian}
      </div>

      <div>
        <h1>New York Times</h1>
        {ny}
      </div>


      <div>
        <h1>NewsAPI</h1>
      {newsApi}
      </div>
    </div>
  )
}
export default Body
