import { createContext, useState } from "react";

export const MyContext = createContext()


const Prov = (props) => {

const [data, setData] = useState([])

  const val = {
    product : data,
    add : (ev) => {

    // already exist
    const alreadyExist = data.find(eve => { return ev.id === eve.id })

     if(!alreadyExist){
       setData(eve => {
         return [...eve, ev]
        });
      }
    },
    del: (ev) => {
      console.log(ev);
    }
  }

  return(
    <MyContext.Provider value={val}>
     {props.children}
  </MyContext.Provider>
    )
}

export default Prov
