import {useEffect, useState} from 'react'
import "./Post.css"

function Post(){
  // upload image
  const [file, setFile] = useState()
  const [preview, setPreview] = useState()

  const [show, setShow] = useState(0)
  const [anim, setAnim] = useState('anim')

  // upload image
  useEffect(() => {
    if(!file) return 
     const fileReader = new FileReader()
     fileReader.readAsDataURL(file)
     fileReader.onload = () => {
       setPreview(fileReader.result)
     }
  },[file])

  console.log(show);

  return <div className="post">

    {(show === 0 || show === 1) && <div className="div"  onClick={() => setShow(1)}>
    <input type="file" onChange={(ev) => setFile(ev.target.files[0])}/>
    </div> }

   {/* upload image */}
   {preview && <img src={preview} alt='upload' /> }

    {show === 0 && <h1>OR</h1> }

   {(show === 0 || show === 2) && <div className="div" onClick={() => setShow(2)}>
    <div>upload your song or <h1>beat</h1></div>
    </div>}

  </div>
}
export default Post
