import axios from "axios";
import { URL } from "../store/URL";



const getS = async () => {
  const response = await axios.get(`${URL}/product/get`);
  return response.data;
};


const newBookS = async(data) => {
  // const response = await axios.post(`${URL}/product/new-book`,data)
  const response = await axios.post("http://localhost:4000/product/new-book", data)
  console.log('service', data);
  return response.data
}
const addToCartS = async(data) => {
  const response = await axios.post(`${URL}/product/add-to-cart`,data)
  console.log('service', data);
  return response.data
}



const productService = {
  newBookS,
  addToCartS,
  getS
}

export default productService
