import { useState } from 'react'
import cup from '../images/cup'
import bmw from '../images/bmw.jpg'
import benz from '../images/benz.jpg'

function Main() {

    const [load, setLoad] = useState(false)

  const data = [
    {id:"A1", name:'BMW', price:'90000', image:bmw, description:"Some details about the product"},
    {id:"A2", name:'BENZ', price:'110000', image: benz, description:"Some details about the product"},

]


    return (
        <div className="main">

        {/* // / BOX 1 ///  */}
            <div className="main-box">
            {data.map(ev => 
            <>
            <div className="main-box1" key={ev.id}>
                <img src={ev.image} className='main-box1-image'/>
                <p>{ev.name}</p>
                <p>{ev.price}</p>
                </div>

                <div className='d-f'>
                <p>{ev.description}</p>

                <div className="d-f">
                <button>-</button> <button>+</button>
                <input type="number" defaultValue={1} min={1} max={5} style={{width:"30px"}}/>
                </div>

                </div>
            </>
            )}
            </div>
        {/* /// BOX 2 ///  */}

            <div className="main-box"></div>


        {/* /// BOX 3 ///  */}

            <div className="main-box">
            {/* {load &&  */}
                {/* <> */}
                <img src={cup} />
                <button>Load Data</button>
                <p>These are some texts</p>
               {load ? <p>Amazon cup,</p> : <p className='load'>nothing</p>}
                <p>The best product provided.</p>
                {/* </> */}
            {/* } */}
            </div>
        </div>
    )
}
export default Main
