import { useState } from 'react'
import style from './MainBox4.module.css'


function MainBox4() {
    const [input1, setInput1] = useState("")
    const [input2, setInput2] = useState("")
    const [data1, setData1] = useState([])
    const [data2, setData2] = useState([])

    const inputHandler1 = (ev) => {
        setInput1(ev.target.value)
    }
    const inputHandler2 = (ev) => {
        setInput2(ev.target.value)
    }

    const submitHandler1 = (ev) => {
        ev.preventDefault()
        if (input1){
            setData1(even => {
                return [ input1 ,...even]
            })
            ev.target.reset()
        }
    }
    const submitHandler2 = (ev) => {
        ev.preventDefault()
        if (input2){
            setData2(even => {
                return [ input2 ,...even]
            })
            ev.target.reset()
        }
    }

    return(
        <div className={style.main}>

        {/* User One */}
            <div className={style.box}>
                User One
              <div className={style.chat}>

                <form onSubmit={submitHandler1}>

                <input onChange={(ev) => inputHandler1(ev)}/>
                {data1.map((ev,i) => <p className={style.data1} key={i}>{ev}</p>)}
                {data2.map((ev,i) => <p className={style.data2} key={i}>{ev}</p>)}

                </form>

              </div>
            </div>

        {/* User Two */}
            <div className={style.box}>
                User Two
              <div className={style.chat}>

                <form onSubmit={submitHandler2}>

                <input onChange={(ev) => inputHandler2(ev)}/>
                {data2.map((ev,i) => <p className={style.data2} key={i}>{ev}</p>)}
                {data1.map((ev,i) => <p className={style.data1} key={i}>{ev}</p>)}

                </form>

              </div>
            </div>
        </div>

    )
}
export default MainBox4
