import {useRef, useState} from 'react'
import './Register.css'

function Register(){
  const [color, setColor] = useState('')
  const [secure, setSecure] = useState(undefined)
  const ref = useRef()


  const passHandler = (ev) => {
    const pass = ev.target.value
    if(pass.length === 0){
      setColor('')
    }
    if(pass.length <= 4 && pass.length > 0) {
      setSecure(0)
      setColor('weak')
    }
    if(pass.length > 4 && pass.length <= 8){
      setSecure(1)
      setColor('moderate')
    }
    if(pass.length > 8){
      setSecure(2)
      setColor('strong')
    }
  }

  const submitHandler = () => {
    console.log('sub');
  }

  const nameHandler = () => {
    console.log(ref);
  }

  return (
    <form className="register" onSubmit={submitHandler}>

      <h1>Register</h1>
      <input placeholder="Name" useRef={ref} onChange={nameHandler}/>
      <input placeholder="Email"/>
      <input placeholder="Password" onChange={(ev) => passHandler(ev)} />

      <div className="pass" >
      <div className={color} />
      <div className={secure && color} />
      <div className={secure === 2 ? color : ''} />
      </div>

      <input placeholder="Password"/>

      <button>Register</button>

    </form>
  )
}
export default Register
