import {createAsyncThunk, createSlice} from '@reduxjs/toolkit'
const userService = require('./userService')

export const getUsers = createAsyncThunk("get-all-users", async(data, thankYou) =>{
  try {
    console.log('slice,', data);
    return await userService.getUsersS(data);
  } catch (error) {
    return thankYou.rejectWithValue(error);
  }
}
);

 export const createUser = createAsyncThunk( "create-user", async (data, thankYou) =>{
    try {
      return await userService.createAccount(data);
    } catch (error) {
      return thankYou.rejectWithValue(error);
    }
  }
);

const initialState = {
  user: [],
  isLoading: false,
  isError: false,
  isSuccess: false,
  message: ''
}

const userSlice = createSlice({
  name:'user',
  initialState,
  reducers: {},
  extraReducers: (builder) =>{
    builder
      .addCase(createUser.pending, (state, action) => {
         state.isLoading = true
      })
      .addCase(createUser.fulfilled, (state, action) => {
        state.isSuccess = true
        state.isError = false
        state.isLoading = false
        state.user = action.payload
      })
      .addCase(createUser.rejected, (state, action) => {
        state.isLoading = false;
        state.isSuccess = false;
        state.isError = true;
      })


      .addCase(getUsers.pending, (state, action) => {
        state.isLoading = true
     })
     .addCase(getUsers.fulfilled, (state, action) => {
       state.isSuccess = true
       state.isError = false
       state.isLoading = false
       state.user = action.payload
     })
     .addCase(getUsers.rejected, (state, action) => {
       state.isLoading = false;
       state.isSuccess = false;
       state.isError = true;
       state.message = action
     })
  }
})

export default userSlice.reducer
