import {useRef, useState} from 'react'
import './Register.css'

function Register(){
  const [color, setColor] = useState('')
  const [secure, setSecure] = useState(undefined)
  const [pass1, setPass1] = useState(false)
  const pass2Ref = useRef()
  const [foc, setFoc] = useState(false) 

  const nameRef = useRef()
  const [email, setEmail] = useState('')
  const [err, setErr] = useState('')

  const passHandler = (ev) => {
    const pass = ev.target.value
    if(pass.length === 0){
      setColor('')
      setPass1('')
    }
    if(pass.length <= 4 && pass.length > 0) {
      setSecure(0)
      setColor('weak')
      setPass1('')
    }
    if(pass.length > 4 && pass.length <= 8){
      setSecure(1)
      setColor('moderate')
      setPass1(ev.target.value)
    }
    if(pass.length > 8){
      setSecure(2)
      setColor('strong')
      setPass1(ev.target.value)
    }
  }

  const submitHandler = (ev) => {
    ev.preventDefault()
    setErr('')

    if (!nameRef.current.value) return setErr('Name needed')

    if (!email.includes('@')) return setErr('Enter a valid email')
    const lowEmail = email.toLocaleLowerCase()

    if (!pass1) return setErr('Your password is weak(at least 5 character)')

    const pass2 = pass2Ref.current.value
    if (pass1 !== pass2){
      return setErr('Password does not match')
    }

    const info = {name: nameRef.current.value, email: lowEmail, password: pass2 }
    console.log(info);
    setErr('')
  }

  const emailHandler = (ev) => {
    setEmail(ev.target.value);
  }

  return (<div className='body'>

      <h1>Register</h1>
    <form className="register" onSubmit={submitHandler}>

      <input placeholder="Name" ref={nameRef} />
      <input placeholder="Email" onChange={(ev) => emailHandler(ev)}/>

      <input placeholder="Password" onChange={(ev) => passHandler(ev)} onFocus={() => setFoc(true)} onBlur={() => setFoc(false)}/> 
     {foc &&  <div className="pass" >
      <div className={color} />
      <div className={secure && color} />
      <div className={secure === 2 ? color : ''} />
      </div>}

      <input placeholder="Password" ref={pass2Ref}/>


      <p className='err'>{err}</p>

      <button>Register</button>

    </form>
  </div>
  )
}
export default Register
