import {useState} from 'react'
import './Register.css'

function Register(){
  const [secure, setSecure] = useState('')

  const passHandler = (ev) => {
    const pass = ev.target.value
    console.log(pass.length);
    if(pass.length === 0){
      setSecure('')
    }
    if(pass.length <= 4 && pass.length > 0) {
      setSecure('weak')
    }
    if(pass.length > 4 && pass.length <= 8){
      setSecure('moderate')
    }
    if(pass.length > 8){
      setSecure('strong')
    }

  }

  return (
    <div className="register">

      <h1>Register</h1>
      <input placeholder="Name"/>
      <input placeholder="Email"/>
      <input placeholder="Password" onChange={(ev) => passHandler(ev)} />

      <div className="pass" >
      <div className={secure ? secure : 'white'} />
      <div className={secure === 'moderate' ? secure : 'white'} />
      <div className={secure === 'strong' ? secure : 'white'} />
      {/* <div className={secure === 'strong' ? secure : 'white'} /> */}
      </div>

      <input placeholder="Password"/>

    </div>
  )
}
export default Register
