import style from './Product.module.css'
import { useDispatch, useSelector } from 'react-redux'
import { useEffect } from 'react'
import { getProducts } from '../feature/productSlice'
import { useLocation, useNavigate } from 'react-router-dom'

const Product = () => {
  const location = useLocation()
  const dispatch = useDispatch()

  const path = location.pathname
  const productId = path.split("/")[2]

  useEffect(() => {
    dispatch(getProducts())
  }, [])

  const state = useSelector(state => state.product.product)

  const find = state?.find(ev => {
    return ev._id === productId
  })

  return (
    <div className={style.body}>
      { find && 
      <div className={style.map}>
      <img src={find.image} alt='book' />
      <div><h4>Book Name:</h4> <p>{find.book}</p></div>
      <div><h4>Author:</h4> <p>{find.author}</p></div>
      <div><h4>Introduction:</h4> <p>{find.introduction}</p></div>
    </div>
      }
    </div>
  )
}
export default Product
