import productService from "./productService";

const initialState = {
  product: [],
  isError: false,
  isLoading: false,
  isSuccess: false,
}


export const getProducts = createAsyncThunk('getProducts-books', async (thunkAPI) => {
  try {
    return await productService.getS()
  } catch (error) {
    return thunkAPI.rejectWithValue(error)
  }
})

export const addBook = createAsyncThunk('new-book', async (data, thunkAPI) => {
  try {
    return await productService.newBookS(data)
  } catch (error) {
    return thunkAPI.rejectWithValue(error)
  }
})



