import { useEffect} from 'react'
import style from './Cart.module.css'
import { useDispatch, useSelector } from 'react-redux'
import { getProducts } from '../feature/productSlice'
import { deleteFromCart, order } from '../store/userSlice'
import { FaEnvelope } from 'react-icons/fa'

const Cart = () => {
  const user = JSON.parse(localStorage.getItem('user'))
  const dispatch = useDispatch()
  let itemsArr = [];

  useEffect(() => {
    dispatch(getProducts())
  }, [])

  const state = useSelector(state => state.product.product)

  if(!user) return <a href='account'><h1>Sign in</h1></a>
  if(user.cart.length === 0) {
    return (
      <div className={style.productMapBody} style={{border: "1px solid yellow"}}>
        <div id='t-c'>
          <FaEnvelope size="50px" />
          <p>Your cart is empty</p>
          </div>
      </div>
    )
  }

  const deleteHandler =  async(bookId) => {
   await dispatch(deleteFromCart({userId: user._id, bookId}))
    dispatch(getProducts())
  }

  const orderHandler = () => {
  dispatch(order({userId: user._id, items: itemsArr}))
  }

let sum = 0;
  user.cart.map(cId => { state.filter(eve => { if (eve._id === cId.bookId) itemsArr.push(eve)}) })
  const map = itemsArr.map(ev => (
    <div key={ev._id} className={style.map}>
      <img src={ev.image} alt='book' />
      <p>{ev.book}</p>
      <p>{ev.author}</p>
      <h4>${ev.price}</h4>
      <button onClick={() => deleteHandler(ev._id)}>Delete from Cart</button>
      {sum += ev.price}
    </div>
  ))

  return (
    <div className={style.cart} >
      {map}
      <div className='d-f a-c'>
      <h1 id='a-c'>Total Price: <p id='c-r'>&nbsp; ${sum} &nbsp; </p>
      <button onClick={orderHandler}> Order</button>
      </h1>
      </div>
    </div>
  )
}
export default Cart
