import style from './AddProduct.module.css'
import { useRef } from 'react'
import { useDispatch } from 'react-redux'
import { addBook } from '../feature/productSlice'

const AddProduct = () => {
  const dispatch = useDispatch()
  const bookRef = useRef()
  const authorRef = useRef()
  const priceRef = useRef()
  const introRef = useRef()
  const imageRef = useRef()
  const sender = JSON.parse(localStorage.getItem('user'))

  const addHandler = (ev) => {
    ev.preventDefault()
console.log(imageRef.current.value);
    const data = { sender: sender.email, book: bookRef.current.value, author: authorRef.current.value, price: priceRef.current.value, image: imageRef.current.value, introduction: introRef.current.value }
    // dispatch(addBook(data));
  }

  return (
    <div className={style.body}>

     {sender ?
     <form onSubmit={addHandler} className={style.add}>
        <span>
          <p>Book name</p>
          <input ref={bookRef} />
        </span>
        <span>
          <p>Author name</p>
          <input ref={authorRef} />
        </span>
        <span>
          <p>Price</p>
          <input ref={priceRef} />
        </span>
        <span>
          <p>Introduction</p>
          <input ref={introRef} />
        </span>
        <span>
          <p>Image</p>
          <input ref={imageRef} type='file'/>
        </span>
        <button onClick={addHandler}>OK</button>
      </form>
      : 
      <a href='/book/account'>Sign In </a>
    }

    </div>
  )
}
export default AddProduct
