import style from './Search.module.css'
import { useEffect, useState } from 'react'
import {useLocation} from 'react-router-dom'

function Search() {
  const [guardian, setGuardian] = useState()
  const [ny, setNy] = useState()
  const [newsApi, setNewsApi] = useState()
  const location = useLocation()

  const l = location.pathname.split('/')[1]
  const keyword = l.toLocaleLowerCase()

  // FETCHING THE NEWS
  useEffect(() => {

    async function newsData1() {
      const GUARDIAN_API = await fetch(`https://content.guardianapis.com/search?api-key=ac83f541-8a6f-49b6-a8fe-7af22e4f4924&${keyword}`)
      const g = await GUARDIAN_API.json()
       //GUARDIAN
       const guardianMap = g.response.results.map(ev => (
        <div key={ev.id} className={style.title}>
          <p>{ev.webUrl.split('/')[2]}</p>
          <a href={ev.webUrl}>{ev.webTitle}</a>
        </div>
      ))
      setGuardian(guardianMap)
    }
    newsData1()




    async function newsData2() {
      const NY_API = await fetch(`https://api.nytimes.com/svc/search/v2/articlesearch.json?${keyword}&api-key=pFRWaAYSzJcy93VdWbwBz8squ4LkfIeq`)
      const n = await NY_API.json()
       //NEW YORK TIMES
       const nyMap = n.response.docs.map(ev => (
        <div key={ev._id} className={style.title}>
          <p>{ev.web_url.split('/')[2]}</p>
          <a href={ev.web_url}>{ev.abstract}</a>
        </div>
      ))
      setNy(nyMap)
    }
    newsData2()



    async function newsData3() {
      const NEWSAPI_API = await fetch(`https://newsapi.org/v2/everything?${keyword}&apiKey=d7116b1cbcfd4fdd9e28ca60ba30ec7c`)
      const na = await NEWSAPI_API.json()
      // NEWS_API
      const newsApiMap = na.articles.map(ev => (
        <div key={Math.random()} className={style.title}>
          <a href={ev.url}>{ev.title}</a>
        </div>
      ))
      setNewsApi(newsApiMap)

    }
    newsData3()

  }, [])

  if(!guardian && !ny && !newsApi) return

  return (
    <div className={style.body}>

<div style={{display:'flex'}}>
      <h1>Source</h1>
      <select>
        <option>date</option>
        <option>category</option>
        <option>resourse</option>
      </select>
</div>


      <div>
        {guardian}
      </div>

      <div>
        {ny}
      </div>


      <div>
      {newsApi}
      </div>
    </div>
  )
}
export default Search
